job.script:
function spooler_process() {
    if (spooler_task.order.params.value("A") != "TEST-A") throw new Error("TEST-A expected")
    var value = spooler_task.order.params.value("TEST")
    spooler_log.info("TEST=" + value)
    if (value != "TEST-200") throw new Error("TEST-200 expected")
    return true
}
