job.script:

@echo %SCHEDULER_JOB_NAME% : job starting
set /a number=%random% %%6 +1
@echo %SCHEDULER_JOB_NAME% : reading job parameters set by previous jobs steps.....
@echo .                                      
echo BOOKING_DATE = %SCHEDULER_PARAM_BOOKING_DATE%
echo START_TRX_ID = %SCHEDULER_PARAM_START_TRX_ID%
@echo .                                      
@echo %SCHEDULER_JOB_NAME% : sleeping for %number% seconds.....
@echo .                                      
ping 127.0.0.1 -n %number% > nul
@echo .                                      
@echo .                                      
@echo %SCHEDULER_JOB_NAME% : job ending
@echo .                                      
@echo .
