job.script:

@echo %SCHEDULER_JOB_NAME% : job starting
set /a number=%random% %%6 +1
@echo %SCHEDULER_JOB_NAME% : saving job parameters for subsequent job steps ...
@echo .                                      
echo BOOKING_DATE=20140705 >>%scheduler_return_values%
echo START_TRX_ID=334233232 >>%scheduler_return_values%
@echo .                                      
@echo %SCHEDULER_JOB_NAME% : sleeping for %number% seconds.....
@echo .                                      
ping 127.0.0.1 -n %number% > nul
@echo .                                      
@echo .                                      
@echo %SCHEDULER_JOB_NAME% : job ending
@echo .                                      
@echo .
