job.script:
function spooler_process() {
    var op = spooler_task.order.web_service_operation
    //String request_string = new String( request.binary_content(), request.charset_name() );
    var response = op.response
    response.content_type = "text/plain"
    response.charset_name = "UTF-8"
    response.string_content = "Bye!"
    response.send()
}
