object_set_class.script:
use diagnostics;
use Win32::OLE;

$spooler_log->msg( "*Job Perl" );


{
    package Testobjekt;


    sub construct
    {
        $main::spooler_log->msg( "Testobjekt::construct" );
        my $self = {};
        bless( $self );
        $self->{name} = shift;
        $self->{level} = 100;
        return $self;
    }


    sub process
    {
        my $self = shift;
        my $new_level = shift;  # Wie holt man den Parameter? Joacim
        $main::spooler_log->msg( "process " . $self->{name} . ", " . $self->{level} . " ==> $new_level" );
        $self->{level} = $new_level;
    }
}



{
    package Object_set_class;


    sub construct
    {
        my $self = {};
        bless( $self );

        $self->{index} = 1;
        $self->{low_level} = 0;
        $self->{high_level} = 0;

        return $self;
    }


    sub spooler_open
    {
        my $self = shift;
        $main::spooler_log->msg( "spooler_open [" . $self->{spooler_low_level} . "," . $self->{spooler_high_level} . ")" );
    }


    sub spooler_close
    {
    }


    sub spooler_get
    {
        my $self = shift;

        if( $self->spooler_eof() )  { return; }

        my $object = Testobjekt::construct( $self->{index} );
        $main::spooler_log->msg( "index=" . $self->{index} . ", level=" . $object->{level} );
        $self->{index}++;

        return $object;
    }


    sub spooler_eof
    {
        my $self = shift;
        if( $self->{index} > 3 ) { return 1; }
        return 0;
    }
}



sub spooler_make_object_set()
{
    my $object_set_class = construct Object_set_class;
    return $object_set_class;
}



# Folgender prozeduraler Code, weil PerlScript kein IDispatch, also kein Objekt liefern kann:

my $object_set_class;
my $object;

sub spooler_dont_use_objects() 
{ 
    $object_set_class = spooler_make_object_set();
    return; 
}

sub spooler_init()
{
    return;
}

sub spooler_open()
{
    $object_set_class->{spooler_low_level} = $spooler_task->object_set->{low_level};
    $object_set_class->{spooler_high_level} = $spooler_task->object_set->{high_level};
    $object_set_class->spooler_open();
    return; 
}

sub spooler_close()
{
    $object_set_class->spooler_open();
    return; 
}

sub spooler_get()
{
    $object = $object_set_class->spooler_get();
    return; 
}

sub spooler_eof()
{
    return $object_set_class->spooler_eof();
}

sub spooler_process()
{
    my $new_level = shift;
    $object->process( $new_level );
    return; 
}
