job.script:
        
	var hostware = new ActiveXObject("hostware.global");
	var i = 0;

	function spooler_process() 
	{
	  i++;
		spooler_log.info("process startet");
	  hostware.sleep(60)
		spooler_log.info("process ended");
		if (i > 5) return false
		return true; /* order=no und return true => Job-Scheduler ruft spooler_process immer wieder auf. */
	}
