job.script:
        

function spooler_process() 
{
    if( spooler_task.try_lock( "Georgien" )  &&
        spooler_task.try_lock( "Venezuela" )    )
    {
        var hostware = new ActiveXObject( "hostware.global" );
        hostware.sleep( 30 );
    }
    else
        spooler_task.call_me_again_when_locks_available();
    
    return false;
}
